from flask import Flask, render_template

app = Flask(__name__)

class Movie:
    def __init__(self,title,img):
        self.title=title
        self.img=img
movie1 = Movie("OLD DOG","http://www.theeldonhouse.com/wordpress/wp-content/uploads/2014/01/old-dog-560x250.jpg")
movie2 = Movie("OLD CAT","data:image/jpeg;base64,")
list_movie = [
    movie1,
    movie2,
    Movie("Hua Hu", "data:image/jpeg;base64,")
]
@app.route('/ok')
def hello_ok():
    return  "Hello OK!"

@app.route('/ok/1')
def hello_team1():
    return "Hello team 1"
@app.route('/<name>')
def hello(name):
    return  ("Hello " + name)
@app.route('/movie')
def get_movie():
    return render_template('movie.html')
@app.route('/')
def index():
    return render_template('index.html')
@app.route('/movie2')
def get_movie2():
    return render_template('movie2.html',title = "old dog",img = "http://www.4licensingcorp.com/wp-content/uploads/2013/08/the_dog_1-516x355.jpg")
@app.route('/movie3')
def movie3():
    return render_template('movie3.html',movie_list = list_movie)
if __name__ == '__main__':
    app.run()
